mapper.select:
SELECT c.id, c.content, c.member_id, m.name, c.created_timestamp
FROM comment AS c
         INNER JOIN member AS m ON c.member_id = m.id
WHERE post_id = #{postId}
ORDER BY c.created_timestamp;

mapper.update:
UPDATE comment
SET content = #{content}
WHERE id = #{commentId} AND member_id = #{memberId}
