mapper.select:
SELECT p.created_timestamp
       c.id,
       c.content,
       c.writer_id,
       m.name,
       c.created_timestamp
FROM comment c
         INNER JOIN post p ON c.post_id = p.id
         LEFT OUTER JOIN member m ON c.writer_id = m.id
WHERE post_id = #{postId}

  AND c.id < #{lastCommentId}

ORDER BY c.id DESC;
LIMIT #{searchSize}

mapper.update:
UPDATE comment
SET content = #{content}
WHERE id = #{commentId}
  AND post_id = #{postId}
  AND writer_id = #{requesterId}

mapper.delete:
DELETE
FROM comment
WHERE id = #{commentId}
  AND post_id = #{postId}
  AND writer_id = #{requesterId}
