mapper.delete:
DELETE
FROM follow
WHERE follower_id = #{request.followerId}
  AND followed_id = #{request.followedId}

mapper.select:
SELECT m.id,
       m.name,
       IF(f2.follower_id IS NOT NULL, TRUE, FALSE) AS is_following
FROM follow f1
         INNER JOIN member m ON f1.follower_id = m.id
         LEFT OUTER JOIN follow f2 ON m.id = f2.followed_id AND f2.follower_id = #{cond.searcherId}
WHERE f1.followed_id = #{cond.followedId}

    AND m.id > #{cond.lastFollowerId}

ORDER BY m.id
LIMIT #{cond.limit}

SELECT m.id,
       m.name,
       IF(f2.follower_id IS NOT NULL, TRUE, FALSE) AS is_following
FROM follow f1
         INNER JOIN member m ON f1.followed_id = m.id
         LEFT OUTER JOIN follow f2 ON m.id = f2.followed_id AND f2.follower_id = #{cond.searcherId}
WHERE f1.follower_id = #{followerId}

    AND m.id > #{cond.lastFollowingId}

ORDER BY m.id
LIMIT #{cond.limit}
