mapper.delete:
DELETE
FROM follow
WHERE follower_id = #{followerId} AND followee_id = #{followeeId}

mapper.select:
SELECT m.id,
       m.name,
       IF(f2.follower_id IS NOT NULL, TRUE, FALSE) AS is_following
FROM follow f1
         INNER JOIN member m ON f1.follower_id = m.id
         LEFT OUTER JOIN follow f2 ON m.id = f2.followee_id AND f2.follower_id = #{searcherId}
WHERE f1.followee_id = #{followeeId}

    AND m.id > #{lastFollowerId}

ORDER BY m.id
LIMIT 100

