         LEFT OUTER JOIN follow f2 ON m.id = f2.followee_id AND f2.follower_id = #{cond.searcherId}
WHERE f1.follower_id = #{followerId}

    AND m.id > #{cond.lastFolloweeId}

ORDER BY m.id
LIMIT #{cond.limit}
