mapper.select:
SELECT p.id, p.title, p.member_id, m.name, p.category_id, p.view_count, p.like_count, p.comment_count, p.created_timestamp, false as is_hot FROM post p LEFT OUTER JOIN member m ON p.member_id = m.id
p.category_id = #{searchCond.categoryId}
p.member_id = #{searchCond.memberId}
AND p.id < #{searchCond.lastPostId}
GROUP BY p.id ORDER BY p.id DESC LIMIT #{limit}

mapper.update:
UPDATE post SET view_count = CASE
WHEN id = #{request.postId} THEN view_count + #{request.viewCount}
ELSE view_count END WHERE id IN
#{request.postId}

UPDATE post
SET like_count = like_count + #{likeCount}
WHERE id = #{postId}

UPDATE post
SET comment_count = comment_count + #{count}
WHERE id = #{postId}

UPDATE post
SET comment_count = comment_count - #{count}
WHERE id = #{postId}

UPDATE post
SET title       = #{request.title},
    content     = #{request.content},
    category_id = #{request.newCategoryId}
WHERE id = #{request.postId}
  AND member_id = #{request.memberId}

mapper.delete:
DELETE
FROM post
WHERE id = #{request.postId}
  AND member_id = #{request.memberId}
