mapper.select:
SELECT p.ID, p.title, p.member_id, m.name, p.created_timestamp, p.view, count(l.post_id) AS likes FROM post AS p LEFT OUTER JOIN member m ON p.member_id = m.id LEFT OUTER JOIN likes AS l ON p.id = l.post_id WHERE p.category_id = #{categoryId}
AND p.id < #{lastPostId}
GROUP BY p.id ORDER BY p.id DESC LIMIT 20
