mapper.select:
SELECT p.id, p.title, p.writer_id, u.name, p.category_id, p.view_count, p.like_count, p.comment_count, p.created_timestamp, TRUE AS is_hot FROM hot_post h INNER JOIN post p ON h.post_id = p.id LEFT OUTER JOIN user u on p.writer_id = u.id
WHERE p.id < #{lastPostId}
ORDER BY p.id DESC LIMIT #{searchSize}

SELECT p.id, p.title, p.writer_id, u.name, p.category_id, p.view_count, p.like_count, p.comment_count, p.created_timestamp, FALSE AS is_hot FROM post p LEFT OUTER JOIN user u ON p.writer_id = u.id WHERE p.category_id = #{categoryId}
AND p.id < #{lastPostId}
ORDER BY p.id DESC LIMIT #{searchSize}

SELECT p.id, p.title, p.writer_id, u.name, p.category_id, p.view_count, p.like_count, p.comment_count, p.created_timestamp, FALSE AS is_hot FROM post p INNER JOIN user u ON p.writer_id = u.id WHERE u.id = #{writerId}
AND p.id < #{lastPostId}
ORDER BY p.id DESC LIMIT #{searchSize}

mapper.update:
UPDATE post
SET view_count = view_count + #{count}
WHERE id = #{postId}

UPDATE post
SET like_count = like_count + #{count}
WHERE id = #{postId}

UPDATE post
SET comment_count = comment_count + #{count}
WHERE id = #{postId}

UPDATE post
SET comment_count = comment_count - #{count}
WHERE id = #{postId}

UPDATE post
SET title = #{title},
    content = #{content},
    category_id = #{categoryId}
WHERE id = #{postId}
  AND writer_id = #{requesterId}

mapper.delete:
DELETE
FROM post
WHERE id = #{postId}
  AND writer_id = #{requesterId}
