mapper.select:
SELECT p.id, p.title, p.member_id, m.name, p.category_id, p.view, p.created_timestamp, COUNT(l.post_id) AS like_count, COUNT(c.id) AS comment_count, false as is_hot FROM post p LEFT OUTER JOIN member m ON p.member_id = m.id LEFT OUTER JOIN likes l ON p.id = l.post_id LEFT OUTER JOIN comment c ON p.id = c.post_id
p.category_id = #{searchCond.categoryId}
p.member_id = #{searchCond.memberId}
AND p.id < #{searchCond.lastPostId}
GROUP BY p.id ORDER BY p.id DESC LIMIT #{limit}

mapper.update:
UPDATE post SET view = CASE
WHEN id = #{request.postId} THEN view + #{request.view}
ELSE view END WHERE id IN
#{request.postId}

UPDATE post
SET title       = #{request.title},
    content     = #{request.content},
    category_id = #{request.newCategoryId}
WHERE id = #{request.postId}
  AND member_id = #{request.memberId}

mapper.delete:
DELETE
FROM post
WHERE id = #{request.postId}
  AND member_id = #{request.memberId}

mapper.select:
SELECT member_id
FROM post
WHERE id = #{postId}
