mapper.select:
SELECT p.id, p.title, p.writer_id, m.name, p.category_id, p.view_count, p.like_count, p.comment_count, p.created_timestamp, TRUE AS is_hot FROM hot_post h INNER JOIN post p ON h.post_id = p.id LEFT OUTER JOIN member m on p.writer_id = m.id
WHERE p.id < #{lastPostId}
ORDER BY p.id DESC LIMIT #{searchSize}

SELECT p.id, p.title, p.writer_id, m.name, p.category_id, p.view_count, p.like_count, p.comment_count, p.created_timestamp, FALSE AS is_hot FROM post p LEFT OUTER JOIN member m ON p.writer_id = m.id WHERE p.category_id = #{categoryId}
AND p.id < #{lastPostId}
ORDER BY p.id DESC LIMIT #{searchSize}

SELECT p.id, p.title, p.writer_id, m.name, p.category_id, p.view_count, p.like_count, p.comment_count, p.created_timestamp, FALSE AS is_hot FROM post p INNER JOIN member m ON p.writer_id = m.id WHERE m.id = #{writerId}
AND p.id < #{lastPostId}
ORDER BY p.id DESC LIMIT #{searchSize}

SELECT writer_id
FROM post
WHERE id = #{postId}

mapper.update:
UPDATE post
SET view_count = view_count + #{viewCount}
WHERE id = #{postId}

UPDATE post
SET like_count = like_count + #{likeCount}
WHERE id = #{postId}

UPDATE post
SET comment_count = comment_count + #{count}
WHERE id = #{postId}

UPDATE post
SET comment_count = comment_count - #{count}
WHERE id = #{postId}

UPDATE post
SET title = #{title},
    content = #{content},
    category_id = #{categoryId}
WHERE id = #{postId}
  AND writer_id = #{requesterId}

mapper.delete:
DELETE
FROM post
WHERE id = #{postId}
  AND writer_id = #{requesterId}
